         ;;

         --down|-d)
            _down="1"
            __optionWithValue=down
         ;;

         --left|-l)
            _left="1"
            __optionWithValue=left
         ;;

         --right|-r)
            _right="1"
            __optionWithValue=right
         ;;

         --assign)
            _assign="@@##@@"
            __optionWithValue=assign
         ;;

         --set)
            _set="@@##@@"
            __optionWithValue=set
