            return 64
           ;;

         *)
            case $__optionWithValue in
               up)
                  _up=$__arg
                  __optionWithValue=
                 ;;
               down)
                  _down=$__arg
                  __optionWithValue=
                 ;;
               left)
                  _left=$__arg
                  __optionWithValue=
                 ;;
               right)
                  _right=$__arg
                  __optionWithValue=
                 ;;
               assign)
                  _assign=$__arg
                  __optionWithValue=
                 ;;
               set)
                  _set=$__arg
                  __optionWithValue=
                 ;;
               fd)
                  _fd=$__arg
                  __optionWithValue=
                 ;;
